    def tambahAcara(self):
        # Ambil data dari UI
        event_name = self.lineEdit.text()
        event_date = self.dateEdit.date().toString("yyyy-MM-dd")
        event_time = self.timeEdit.time().toString("hh:mm")
        event_description = self.textEdit.toPlainText()

        # Validasi data
        if not event_name or not event_date or not event_time:
            QMessageBox.critical(self.centralwidget, "Error", "Nama Acara, Tanggal, dan Waktu harus diisi.")
            return

        try:
            # Konfigurasi koneksi ke database MySQL
            db_config = {
                'host': 'localhost',
                'user': 'root',
                'password': '',
                'database': 'jadwal_acara',
            }

            # Membuat koneksi
            connection = mysql.connector.connect(**db_config)

            # Membuat kursor
            cursor = connection.cursor()

            # Query untuk menyimpan data
            query = "INSERT INTO acara (Nama_Acara, Tanggal, Waktu, Deskripsi) VALUES (%s, %s, %s, %s)"
            values = (event_name, event_date, event_time, event_description)

            # Eksekusi query
            cursor.execute(query, values)

            # Commit perubahan ke database
            connection.commit()

            # Menutup kursor dan koneksi
            cursor.close()
            connection.close()

            # Mengatur nilai default setelah acara ditambahkan
            self.lineEdit.clear()
            self.dateEdit.setDate(QtCore.QDate.currentDate())
            self.timeEdit.setTime(QtCore.QTime.currentTime())
            self.textEdit.clear()

            # Memberikan notifikasi bahwa acara berhasil ditambahkan
            QMessageBox.information(self.centralwidget, "Sukses", "Acara berhasil ditambahkan ke database.")
        except Exception as e:
            # Menampilkan pesan error jika terjadi masalah
            QMessageBox.critical(self.centralwidget, "Error", f"Terjadi kesalahan: {str(e)}")

    def showNotificationFromNotifierApp(self):
        # Cek apakah NotifierApp sudah dibuat sebelumnya
        if not hasattr(self, 'notifier_app'):
            # Jika belum, buat instance NotifierApp baru
            self.notifier_app = NotifierApp()

            # Extract relevant data from Ui_MainWindow
            event_name = self.lineEdit.text()
            event_date = self.dateEdit.date().toString("yyyy-MM-dd")
            event_time = self.timeEdit.time().toString("hh:mm")
            event_description = self.textEdit.toPlainText()

            # Set the data in NotifierApp
            self.notifier_app.title.setText(event_name)
            self.notifier_app.msg.setText(event_description)

            # Concatenate event date and time and set in NotifierApp datetime_edit
            event_datetime = f"{event_date} {event_time}"
            self.notifier_app.datetime_edit.setDateTime(QtCore.QDateTime.fromString(event_datetime, "yyyy-MM-dd hh:mm"))

        # Cek apakah sudah ada koneksi pada tombol
        if hasattr(self, 'timer_connection'):
            # Jika ada, lepaskan koneksi yang lama
            self.pushButton.clicked.disconnect(self.timer_connection)

            # Connect the button to start the QTimer
            self.timer_connection = self.pushButton.clicked.connect(self.notifier_app.show_notification)

        # Show the NotifierApp window
        self.notifier_app.show()

